// Import your database functions
const { insertProduct } = require('./functions/add-new-products');
const { deleteProductById } = require("./functions/delete-exisitng-products");

const app = express();

// Use body-parser middleware to parse JSON requests
app.use(bodyParser.json());

// Serve the React build folder as static files
app.use(express.static(path.join(__dirname)));

// API route to create a new product
app.post('/create-product', async (req, res) => {
    const { name, description, price, stock } = req.body;

    if (!name || !price || stock === undefined) {
        return res.status(400).send('Missing required fields: name, price, or stock');
    }

    try {
        const newProduct = await insertProduct(name, description, price, stock);
        res.status(201).json(newProduct);
    } catch (error) {
        console.error('Error:', error);
        res.status(500).send('Server error');
    }
});

// API route to delete a product by ID
app.delete('/delete-product/:id', async (req, res) => {
    const productID = req.params.id;

    try {
        const result = await deleteProductById(productID);
        if (result.error) {
            return res.status(500).json({ error: 'Failed to delete product' });
        }

        res.status(200).json({ message: 'Product deleted successfully', product: result });
    } catch (error) {
        console.error('Error:', error);
        res.status(500).json({ error: 'Server error' });
    }
});

// API route to fetch all products (for the frontend)
app.get('/products', async (req, res) => {
    try {
        const products = await getAllProducts(); // Define this function to fetch all products from DB
        res.status(200).json(products);
    } catch (error) {
        console.error('Error fetching products:', error);
        res.status(500).json({ error: 'Error fetching products' });
    }
});

// Serve React's index.html for any other route not handled by the API
app.get('*', (req, res) => {
    res.sendFile(path.join(__dirname, 'build', 'index.html'));
});

// Start the server
const port = process.env.PORT || 3000;
app.listen(port, () => {
    console.log(`Server running on port ${port}`);
});

